window.addEventListener("DOMContentLoaded",(function(){headerActive()}));let headerActive=()=>{let e=document.querySelector(".header"),t=document.querySelector(".header__cross");e&&(t.onclick=()=>{e.classList.contains("active")?(e.classList.remove("active"),document.querySelector("body").style.overflow=""):(e.classList.add("active"),document.querySelector("body").style.overflow="hidden")})};