window.addEventListener("DOMContentLoaded",(function(){headerActive()}));let headerActive=()=>{let e=document.querySelector(".header"),t=document.querySelector(".header__cross"),c=document.querySelector("body");e&&(t.onclick=()=>{e.classList.contains("active")?(e.classList.remove("active"),c.style.overflow=""):(e.classList.add("active"),c.style.overflow="hidden")})};