window.addEventListener("DOMContentLoaded",(function(){headerActive(),addClassToLink()}));let headerActive=()=>{let e=document.querySelector(".header"),t=document.querySelector(".header__cross"),o=document.querySelector("body");e&&(t.onclick=()=>{e.classList.contains("active")?(e.classList.remove("active"),o.style.overflow=""):(e.classList.add("active"),o.style.overflow="hidden")})},addClassToLink=()=>{let e=document.querySelectorAll(".how-to__list>li"),t=window.innerHeight/2;e.forEach(e=>{window.addEventListener("scroll",(function(){if(e.getBoundingClientRect().top<=t&&!e.classList.contains("active")){let t=document.querySelector(".how-to__list>li.active");e.classList.add("active"),t&&t.classList.remove("active")}}))})};